{% import "organizer/_BAD_variables.sql" as var -%}use `BADdb`;-- Couples-- Insert Standard and Latin couples into starting lists:{%- for lead in leads %}{%- if lead.competition == "Ballroom" %}    {%- if lead.level == "Breitensport" %}    {%- set tournament = var.tournament_Ballroom_Breitensport_qualification %}    {%- elif lead.level == "CloseD" %}    {%- set tournament = var.tournament_Ballroom_CloseD %}    {%- elif lead.level == "Open Class" %}    {%- set tournament = var.tournament_Ballroom_Open_Class %}    {%- endif %}{%- elif lead.competition == "Latin" %}    {%- if lead.level == "Breitensport" %}    {%- set tournament = var.tournament_Latin_Breitensport_qualification %}    {%- elif lead.level == "CloseD" %}    {%- set tournament = var.tournament_Latin_CloseD %}    {%- elif lead.level == "Open Class" %}    {%- set tournament = var.tournament_Latin_Open_Class %}    {%- endif %}{%- endif %}INSERT INTO `start` (`tourn`,`lead`,`follow`) VALUES ({{ tournament }}, {{ lead.contestant_id }}, {{ lead.partner }});{%- endfor %}-- Insert Standard and Latin CloseD and Open Class into starting lists:-- Leads{%- for lead in closed_open_leads %}{%- if lead.competition == "Ballroom" %}    {%- if lead.level == "CloseD" %}    {%- set tournament = var.tournament_Ballroom_CloseD %}    {%- elif lead.level == "Open Class" %}    {%- set tournament = var.tournament_Ballroom_Open_Class %}    {%- endif %}{%- elif lead.competition == "Latin" %}    {%- if lead.level == "CloseD" %}    {%- set tournament = var.tournament_Latin_CloseD %}    {%- elif lead.level == "Open Class" %}    {%- set tournament = var.tournament_Latin_Open_Class %}    {%- endif %}{%- endif %}INSERT INTO `start` (`tourn`,`lead`) VALUES ({{ tournament }}, {{ lead.contestant_id }});{%- endfor %}-- Follows{%- for follow in closed_open_follows %}{%- if follow.competition == "Ballroom" %}    {%- if follow.level == "CloseD" %}    {%- set tournament = var.tournament_Ballroom_CloseD %}    {%- elif follow.level == "Open Class" %}    {%- set tournament = var.tournament_Ballroom_Open_Class %}    {%- endif %}{%- elif follow.competition == "Latin" %}    {%- if follow.level == "CloseD" %}    {%- set tournament = var.tournament_Latin_CloseD %}    {%- elif follow.level == "Open Class" %}    {%- set tournament = var.tournament_Latin_Open_Class %}    {%- endif %}{%- endif %}INSERT INTO `start` (`tourn`,`follow`) VALUES ({{ tournament }}, {{ follow.contestant_id }});{%- endfor %}-- Insert Salsa couples into starting lists:{%- for couple in salsa_couples %}INSERT INTO `start` (`tourn`,`lead`,`follow`) VALUES ({{ var.tournament_Salsa }}, {{ couple.lead_id }}, {{ couple.follow_id }});{%- endfor %}-- Insert Polka couples into starting lists:{%- for couple in polka_couples %}INSERT INTO `start` (`tourn`,`lead`,`follow`) VALUES ({{ var.tournament_Polka }}, {{ couple.lead_id }}, {{ couple.follow_id }});{%- endfor %}SHOW WARNINGS;